(function() {
    tinymce.ScriptLoader.load('https://desmos.lumenlearning.com/desmos/js/calculator.js');

    function loadDesmos(ed) {
        elt = ed.dom.select('div');
        if (elt.length>0) {
            for (i = 0; i < elt.length; i++) {
                if (elt[i].className == 'js-desmos') {
                    var calculator = Desmos.GraphingCalculator(elt[i]);
                    json = elt[i].getAttribute("data-json");
                    console.log(json);
                    if (json != '') {
                        calculator.setState(json);
                    }
                }
            }
        }
    }

    tinymce.create('tinymce.plugins.desmos', {
        /**
         * Initializes the plugin, this will be executed after the plugin has been created.
         * This call is done before the editor instance has finished it's initialization so use the onInit event
         * of the editor instance to intercept that event.
         *
         * @param {tinymce.Editor} ed Editor instance that the plugin is initialized in.
         * @param {string} url Absolute URL to where the plugin is located.
         */
        init : function(ed, url) {
            ed.addCommand('mceDesmos', function() {
                el = ed.selection.getNode();

                if (el.nodeName == 'FIGURE') {
                    desmosjson = '';
                    if (ed.dom.getAttrib(el,"data-json")!='') {
                        desmosjson = ed.dom.getAttrib(el, "data-json");
                    }
                    isnew = false;
                    elwidth = parseInt(ed.dom.getStyle(el,"width"));
                    elheight = parseInt(ed.dom.getStyle(el,"height"));
                } else {
                    isnew = true;
                    desmosjson = '';
                    elwidth = 300;
                    elheight = 200;
                }
                // Open window
                ed.windowManager.open({
                    title: "Add Desmos Interactive",
                    file : url + '/desmos.htm',
                    width : 800,
                    height : 470,
                    inline : 1
                }, {
                    isnew : isnew, // Custom argument
                    desmosjson : desmosjson,
                    width : elwidth,
                    height : elheight
                });
            });
            // Add a button that opens a window
            ed.addButton('desmos', {
                tooltip: "Add/Edit Graph",
                text: ' Add Desmos',
                cmd : 'mceDesmos',
                image : url + '/img/ed_desmos.png',
                stateSelector: 'figure[data-json]'
            });
            //may use setContent for after adding, this should work with a saved desmos
            ed.on('init', function (e) {
              //loadDesmos(ed);
            });
        },

        /**
         * Returns information about the plugin as a name/value array.
         * The current keys are name, url, author and version.
         *
         * @return {Object} Name/value array containing information about the plugin.
         */
        getInfo : function() {
            return  {
                name: "desmos plugin",
                url: "http://ohm.lumenlearning.com",
                author : 'Alena Holligan',
                infourl : '',
                version : "1.0"
            };
        },
    });

    // Register plugin
    tinymce.PluginManager.add('desmos', tinymce.plugins.desmos);
})();
