(function() {
    tinymce.create('tinymce.plugins.desmos', {
        /**
         * Initializes the plugin, this will be executed after the plugin has been created.
         * This call is done before the editor instance has finished it's initialization so use the onInit event
         * of the editor instance to intercept that event.
         *
         * @param {tinymce.Editor} ed Editor instance that the plugin is initialized in.
         * @param {string} url Absolute URL to where the plugin is located.
         */
        init : function(ed, url) {
            var desmosjson = '';
            var elwidth = 300;
            var elheight = 200;
            ed.addCommand('mceDesmos', function(el) {
                desmosjson = '';
                if (ed.dom.getAttrib(el,"data-json")!='') {
                    desmosjson = ed.dom.getAttrib(el, "data-json");
                }
                elwidth = parseInt(ed.dom.getStyle(el,"width"));
                elheight = parseInt(ed.dom.getStyle(el,"height"));
                // Open window
                ed.windowManager.open({
                    title: "Desmos Graph",
                    file : url + '/desmos.php',
                    width : 800,
                    height : 470,
                    inline : 1
                }, {
                    isnew : false, // Custom argument
                    desmosjson : desmosjson,
                    width : elwidth,
                    height : elheight
                });
            });
            ed.addCommand('mceDesmosNew', function() {
                // Open window
                ed.windowManager.open({
                    title: "Add New Desmos Graph",
                    file : url + '/desmos.php',
                    width : 800,
                    height : 470,
                    inline : 1
                }, {
                    isnew : true, // Custom argument
                    desmosjson : desmosjson,
                    width : elwidth,
                    height : elheight
                });
            });
            ed.addCommand('mceDesmosImport', function() {
                // Open window
                ed.windowManager.open({
                    title: "Import by URL",
                    file : url + '/desmos_import.php',
                    width : 600,
                    height : 130,
                    inline : 1
                });
            });
            // Add a dropdown that opens one of two windows
            ed.addButton('desmos', {
                type: 'menubutton',
                text: 'Add Desmos',
                icon: 'desmos',
                menu: [{
                    tooltip: "New Desmos Graph",
                    text: 'New',
                    stateSelector: 'figure[data-json]',
                    onclick: function() {
                        ed.execCommand('mceDesmosNew');
                    }
                },
                {
                    tooltip: "Import Desmos Graph by URL",
                    text: 'Import by URL',
                    onclick: function() {
                        ed.execCommand('mceDesmosImport');
                    }
                }]
            });
            //may use setContent for after adding, this should work with a saved desmos
            ed.on('loadcontent', function (e) {
                element = ed.dom.doc.getElementById("tinymce");
                if (element.addEventListener) {
                    element.addEventListener("click", func, false);
                } else if (element.attachEvent) {
                    element.attachEvent("onClick", func);
                } else {
                    element['onClick'] = func;
                }
            });
        },

        /**
         * Returns information about the plugin as a name/value array.
         * The current keys are name, url, author and version.
         *
         * @return {Object} Name/value array containing information about the plugin.
         */
        getInfo : function() {
            return  {
                name: "desmos plugin",
                url: "http://ohm.lumenlearning.com",
                author : 'Alena Holligan',
                infourl : '',
                version : "1.0"
            };
        },
    });

    // Register plugin
    tinymce.PluginManager.add('desmos', tinymce.plugins.desmos);
})();
function func(e){
    var parentId = '';
    if (e.path) {
        console.log(e.path);
        parentId = e.path.filter(function (p) {
            return p.nodeName == "BODY";
        })[0].getAttribute("data-id");
    }
    if (e.toElement.offsetParent) {
        console.log(e.toElement.offsetParent);
        parentId = e.toElement.offsetParent.getAttribute("data-id");
    }
    console.log(parentId);
    tinyMCE.get(parentId).focus();
    if(e.target && e.target.nodeName == "FIGURE") {
        tinymce.activeEditor.execCommand('mceDesmos', e.target);
    }
};
